mapper.select:
SELECT * FROM contents
WHERE 1=1

    AND is_deleted = #{isDeleted}


    AND status = #{status}


    AND id IN (
    SELECT content_id
    FROM content_tag
    WHERE tag_id IN

        #{tagId}

    )

ORDER BY id DESC

mapper.update:
UPDATE contents
title = #{title}, cover_image = #{coverImage}, content_body = #{contentBody}, author_id = #{authorId}, view_count = #{viewCount}, comment_count = #{commentCount}, avg_rating = #{avgRating}, rating_count = #{ratingCount}, status = #{status}, is_deleted = #{isDeleted}, comment = #{comment}, updated_at = #{updatedAt},
WHERE id = #{id}

mapper.select:
SELECT COUNT(1) FROM contents
WHERE 1=1

    AND is_deleted = #{isDeleted}


    AND status = #{status}


    AND id IN (
    SELECT content_id
    FROM content_tag
    WHERE tag_id IN

        #{tagId}

    )
